<template>
  <div class="chapter">
    <main>
      <h2 style="text-align: center; font-size: 2em">
        {{ chapterInfo.title }}
      </h2>
      <h3 style="text-align: right; font-size: 0.8em">
        {{ chapterInfo.updateTime }}
      </h3>
      <article>
        <p
          style="text-indent: 2em"
          v-for="item in chapterInfo.content"
          :key="item.id"
        >
          {{ item }}
        </p>
      </article>
      <footer>
        <a-space>
          <a-button
            type="ghost"
            @click="toChapter(chapterInfo.prevChapterId)"
            v-show="chapterInfo.prevChapterId != 0"
            >上一章</a-button
          >
          <a-button
            type="ghost"
            @click="toChapter(chapterInfo.nextChapterId)"
            v-show="chapterInfo.nextChapterId != 0"
            >下一章</a-button
          >
        </a-space>
      </footer>
    </main>
    <aside>
      <a-divider
        ><a-button @click="toBook(chapterInfo.bookId)" type="link"><h2>{{chapterInfo.bookName}}</h2> </a-button>
      </a-divider>
      <a-image
        :width="200"
        :height="200"
        :src="FetchImage(LIB_BOOK_IMAGE,chapterInfo.bookId)"
        fallback="data:image/png;base64,"
      />
      <a-divider><h2>目录</h2></a-divider>
      <a-collapse v-model:activeKey="activeKey" :bordered="false">
        <a-collapse-panel
          :header="item.title"
          v-for="item in catalog"
          :key="item.id"
        >
          <a-button
            type="ghost"
            @click="toChapter(it.id)"
            v-for="it in item.chapters"
            :key="it.id"
          >
            {{ it.title }}
          </a-button>
        </a-collapse-panel>
      </a-collapse>
    </aside>
  </div>
</template>

<script setup>
import { ref } from "@vue/reactivity";
import { useRoute, useRouter } from "vue-router";
import { FetchChapter, FetchVolumesOnBook } from "../../ports/book.js";
import {FetchImage,LIB_BOOK_IMAGE,LIB_USER_AVATOR} from "../../ports/file.js";

const catalog = ref([]);
const activeKey = ref(true);
const chapterInfo = ref({});
const route = useRoute();
const router = useRouter();
const chapterId = route.params.id;

function toChapter(chapter) {
  router.push({ name: "chapter", params: { id: chapter } });
}

function toBook(book){
  router.push({name:"book",params:{id: book}});
}

function goToChapter(chapter) {
  FetchChapter(chapter, (data) => {
    chapterInfo.value = data;
    chapterInfo.value.content = data.content.split("\n");
    FetchVolumesOnBook(chapterInfo.value.bookId, (data) => {
      catalog.value = data;
    });
  });
}

goToChapter(chapterId);
</script>

<style scoped>
p {
  text-indent: 2em;
}

.chapter {
  display: flex;
  width: 80%;
  margin: auto;
}

.chapter main {
  width: 60%;
  margin-right: 10%;
}

.chapter aside {
  width: 20%;
  margin-left: 10%;
}
</style>