<template>
  <div class="socialpage">
    <main>
      <h2>玄沧阁</h2>
      <div style="display: flex">
        <a-image
          :width="200"
          :height="200"
          :src="'https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png?${random}'"
          fallback="data:image/png;base64,"
        />
        <div class="info">
          <h2>负责人: {{ social.masterName }}</h2>
          <h2>创建日期: {{ social.createTime }}</h2>
          <h2>最新活动: {{ social.newestAct }}</h2>
          <h2>最新公告: {{ social.newestAnnounce }}</h2>
        </div>
      </div>
      <div>
        <h2>签名:</h2>
        <p>
          {{ social.moto }}
        </p>
      </div>
      <a-tabs v-model:activeKey="activeKey">
        <a-tab-pane key="1" tab="活动">
          <a-list
            item-layout="vertical"
            :pagination="actPagi"
            side="large"
            :data-source="acts"
          >
            <template #renderItem="{ item }">
              <a-list-item>
                <a-divider
                  ><h2>{{ item.name }}</h2></a-divider
                >
                <p>{{ item.description }}</p>
                <a-list
                  item-layout="vertical"
                  size="large"
                  :data-source="item.books"
                >
                  <template #renderItem="{ item }">
                    <a-list-item key="item.id">
                      <template #extra>
                        <a-image
                          :width="150"
                          :src="'https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png?${random}'"
                          fallback="data:image/png;base64,"
                        />
                      </template>
                      <a-list-item-meta :description="item.authorName">
                        <template #title>
                          <a :href="item.href">{{ item.name }}</a>
                        </template>
                        <template #avatar
                          ><a-avatar
                            :src="'https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png?${random}'"
                        /></template>
                      </a-list-item-meta>
                      {{ item.bookDesc }}
                    </a-list-item>
                  </template>
                </a-list>
              </a-list-item>
            </template>
          </a-list>
          <div v-for="item in acts" :key="item.id"></div>
        </a-tab-pane>
        <a-tab-pane key="2" tab="公告" force-render>
          <a-list
            item-layout="vertical"
            :pagination="announcePagi"
            :data-source="anns"
          >
            <template #renderItem="{ item }">
              <a-list-item key="item.id">
                <a-list-item-meta :description="item.authorName">
                  <template #title>
                    <a :href="item.href">{{ item.title }}</a>
                  </template>
                  <template #avatar><a-avatar :src="item.avatar" /></template>
                </a-list-item-meta>
                {{ item.content }}
              </a-list-item>
            </template>
          </a-list>
        </a-tab-pane>
      </a-tabs>
    </main>
    <aside>
      <a-divider><h1 style="font-size: 2em">社团成员一览</h1></a-divider>
      <a-divider
        ><h2>
          负责人: <a-button type="ghost">{{ members.master.name }}</a-button>
        </h2></a-divider
      >
      <a-divider><h2>管理员</h2></a-divider>
      <a-list
        item-layout="vertical"
        :pagination="adminPagi"
        :data-source="members.admins"
      >
        <template #renderItem="{ item }">
          <a-list-item key="item.id">
            <template #extra>
              <a-image
                :width="150"
                :src="'https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png?${random}'"
                fallback="data:image/png;base64,"
              />
            </template>
            <a-list-item-meta :description="'代表作 ' + item.presentBookName">
              <template #title>
                <a :href="item.href">{{ item.authorName }}</a>
              </template>
              <template #avatar><a-avatar :src="item.avatar" /></template>
            </a-list-item-meta>
          </a-list-item>
        </template>
      </a-list>
      <a-divider><h2>成员</h2></a-divider>
      <a-list
        item-layout="vertical"
        :pagination="memPagi"
        :data-source="members.members"
      >
        <template #renderItem="{ item }">
          <a-list-item key="item.id">
            <template #extra>
              <a-image
                :width="150"
                :src="'https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png?${random}'"
                fallback="data:image/png;base64,"
              />
            </template>
            <a-list-item-meta :description="'代表作 ' + item.presentBookName">
              <template #title>
                <a :href="item.href">{{ item.authorName }}</a>
              </template>
              <template #avatar><a-avatar :src="item.avatar" /></template>
            </a-list-item-meta>
          </a-list-item>
        </template>
      </a-list>
    </aside>
  </div>
</template>

<script setup>
import { ref } from "@vue/reactivity";
import { useRoute } from "vue-router";
import {
  FetchSocialInfo,
  FetchActList,
  FetchAnnList,
  FetchMembers,
} from "../../ports/social.js";

const route = useRoute();
const activeKey = ref("2");
const acts = ref([]);
const anns = ref([]);
const members = ref({});
const social = ref({});
const socialId = route.params.id;

const actPagi = ref({
  onChange: (page) => {
    console.log(page);
  },
  pageSize: 2,
  current: 1,
  total: 1,
});

const announcePagi = ref({
  onChange: (page) => {
    console.log(page);
  },
  pageSize: 10,
  current: 1,
  total: 1,
});

const adminPagi = ref({
  onChange: (page) => {
    console.log(page);
  },
  pageSize: 30,
  current: 1,
  total: 1,
});

const memPagi = ref({
  onChange: (page) => {
    console.log(page);
  },
  pageSize: 30,
  current: 1,
  total: 1,
});

function _re(data, pagi) {
  pagi.value.current = Number.parseInt(data.current);
  pagi.value.total = Number.parseInt(data.total);
}

function Init() {
  members.value = {
    master: {},
    members: [],
    admins: [],
  };
  FetchSocialInfo(socialId, (data) => {
    social.value = data;
    members.value.master = {
      id: data.masterId,
      name: data.masterName,
      presentBookId: data.presentBookId,
      presentBookName: data.presentBookName,
    };
  });
  FetchActList(
    socialId,
    {
      size: actPagi.value.pageSize,
      current: actPagi.value.current,
    },
    (data) => {
      acts.value = [];
      for(let v in data){
        acts.value.push(data[v]);
      }
      _re(data, actPagi);
    }
  );
  FetchAnnList(
    socialId,
    {
      size: announcePagi.value.pageSize,
      current: announcePagi.value.current,
    },
    (data) => {
      anns.value = data.records;
      _re(data, announcePagi);
    }
  );
  FetchMembers(
    socialId,
    "2",
    {
      size: adminPagi.value.pageSize,
      current: adminPagi.value.current,
    },
    (data) => {
      members.value.admins = data.records;
      _re(data, adminPagi);
    }
  );
  FetchMembers(
    socialId,
    "3",
    {
      size: memPagi.value.pageSize,
      current: memPagi.value.current,
    },
    (data) => {
      members.value.members = data.records;
      _re(data, memPagi);
    }
  );
}


Init();
</script>

<style scoped>
.socialpage {
  display: flex;
  width: 80%;
  margin: auto;
}

.socialpage main {
  width: 60%;
  border-right: grey dashed 1px;
}

.socialpage aside {
  width: 30%;
}
</style>
